        public boolean checkIfPangram(String sentence) {
            int[] arr = new int[26];
            char[] chas = sentence.toCharArray();
            for (int i = 0; i < chas.length; i++) arr[chas[i] - 'a']++;
            for (int i = 0; i < arr.length; i++)
                if (arr[i] == 0) return false;
            return true;
        }


